create table "SZERVEZET" (
  "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
  "NAME" VARCHAR NOT NULL
);


create table "MUNKATARS_CSOPORT" (
  "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
  "NAME" VARCHAR NOT NULL,
  "SZERVEZET_ID" INTEGER,
  FOREIGN KEY ("SZERVEZET_ID") REFERENCES "SZERVEZET"("ID")
);

create table "MUNKATARS" (
  "ID" INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 1) NOT NULL PRIMARY KEY,
  "NAME" VARCHAR NOT NULL,
  "ADDRESS" VARCHAR NULL,
  --"BIRTHDAY" DATE,
  "SZERVEZET_ID" INTEGER,
  FOREIGN KEY ("SZERVEZET_ID") REFERENCES "SZERVEZET"("ID"),
  "MUNKATARS_CSOPORT_ID" INTEGER,
  FOREIGN KEY ("MUNKATARS_CSOPORT_ID") REFERENCES "MUNKATARS_CSOPORT"("ID")
);
